          IF NEW.Ingredient2 > 0 AND NEW.Ingredient1 > NEW.Ingredient2
          THEN
            SIGNAL SQLSTATE VALUE '45000'
            SET MESSAGE_TEXT = '[table:synth_recipes] - `Ingredient1` is larger than Ingredient2';
          END IF;

